#include "roman.hpp"
#include "gtest/gtest.h"

using namespace ::testing;

namespace inform {
namespace utils {
namespace test {
namespace {

TEST(RomanNumerals, TestBasics)
{
    ASSERT_EQ("", to_roman_number(0));
    ASSERT_EQ("I", to_roman_number(1));
    ASSERT_EQ("II", to_roman_number(2));
    ASSERT_EQ("III", to_roman_number(3));
    //test list (subject to change):
    //1 ->    "I" | 10 ->    "X" | 100 ->    "C" | 1000 ->    "M"
}

