#ifndef ROMAN_INCLUDED
#define ROMAN_INCLUDED

#include <string>

namespace inform {
namespace utils {

/* Given a positive integer number (eg 42) determine
 * its Roman numeral representation as a String (eg "XLII").
 */
std::string to_roman_number(unsigned);

} //namespace utils
} //namespace inform

#endif
